mapper.select:
SELECT

FROM ss_checking_in a

WHERE a.id = #{id}

SELECT

FROM ss_checking_in a

WHERE a.dateline = #{dateline}

SELECT 

FROM ss_checking_in a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.dateline = #{dateline}


		AND a.am >= #{am}


		AND a.pm >= #{pm}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ss_checking_in a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ss_checking_in SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
