mapper.select:
SELECT 

FROM ss_checking_in_logs a

WHERE a.recordid = #{recordid}

SELECT
FROM ss_checking_in_logs a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.recorddate BETWEEN #{beginRecorddate} AND #{endRecorddate}
AND a.departmentname LIKE '%'||#{departmentname}||'%' '%'+#{departmentname}+'%' concat('%',#{departmentname},'%')
AND a.personname LIKE '%'||#{personname}||'%' '%'+#{personname}+'%' concat('%',#{personname},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM ss_checking_in_logs a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ss_checking_in_logs SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE recordid = #{recordid}
